mapper.insert:
insert into admin_info (cst_create, cst_modified, user_id, stu_serial, job_serial, name, role)
values (current_timestamp(), current_timestamp(), #{userId}, #{stuSerial},
        #{jobSerial}, #{name}, #{role});

mapper.update:
update admin_info
set is_deleted = true
where id = #{id}
  and is_deleted = false;

mapper.select:
select *
from admin_info
where is_deleted = false;

select *
from admin_info t1
where t1.id = #{id}
  and is_deleted = false;
